targets.expr:
(sum(
  avg_over_time(
    kube_pod_status_ready{
      namespace="$namespace",
      runner="$runner",
      organization="$organization",
      condition="true"
    }[5m]
  )
  * on (pod) group_left(label_app_kubernetes_io_kubiya_runner_component)
  kube_pod_labels{
    namespace="$namespace",
    runner="$runner",
    organization="$organization",
    label_app_kubernetes_io_kubiya_runner_component="$component"
  }
) /
sum(
  avg_over_time(
    kube_pod_status_ready{
      namespace="$namespace",
      runner="$runner",
      organization="$organization"
    }[5m]
  )
  * on (pod) group_left(label_app_kubernetes_io_kubiya_runner_component)
  kube_pod_labels{
    namespace="$namespace",
    runner="$runner",
    organization="$organization",
    label_app_kubernetes_io_kubiya_runner_component="$component"
  }
)) * 100

sum(
  rate(
    kube_pod_container_status_restarts_total{
      namespace="$namespace",
      runner="$runner",
      organization="$organization"
    }[5m]
  )
  * on (pod) group_left(label_app_kubernetes_io_kubiya_runner_component)
  kube_pod_labels{
    namespace="$namespace",
    runner="$runner",
    organization="$organization",
    label_app_kubernetes_io_kubiya_runner_component="$component"
  }
) * 300

100 * (
  sum(
    rate(
      agent_manager_http_requests_total{
        status=~"2..",
        runner="$runner",
        app="$component",
        namespace="$namespace",
        organization="$organization"
      }[5m]
    )
  )
  /
  sum(
    rate(
      agent_manager_http_requests_total{
        runner="$runner",
        app="$component",
        namespace="$namespace",
        organization="$organization"
      }[5m]
    )
  )
)

100 * (
  sum(
    rate(
      response_status{
        status=~"2..",
        runner=~"$runner",
        app="$component",
        namespace="$namespace",
        organization="$organization"
      }[5m]
    )
  )
  /
  sum(
    rate(
      http_requests_total{
        runner="$runner",
        app="$component",
        namespace="$namespace",
        organization="$organization"
      }[5m]
    )
  )
)
